import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Tooltip,
  Legend,
} from "chart.js";
import ChartDataLabels from "chartjs-plugin-datalabels";
import { Line } from "react-chartjs-2";
import "./PriceAnalytics.css";

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Tooltip,
  Legend,
  ChartDataLabels
);

const API_BASE = "https://grocerrybackend.onrender.com/api";

export default function PriceAnalytics() {
  const [loading, setLoading] = useState(true);
  const [productList, setProductList] = useState([]);
  const [categoryList, setCategoryList] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("");
  const [chartData, setChartData] = useState(null);

  /* ================= LOAD DROPDOWNS ================= */
  useEffect(() => {
    Promise.all([
      axios.get(`${API_BASE}/prices`),
      axios.get(`${API_BASE}/categories`),
    ]).then(([priceRes, catRes]) => {
      // ðŸ”¥ Extract ONLY PRODUCTS from nested structure
      const products = [];

      // priceRes.data.data.forEach(category => {
      //   category.subcategories.forEach(sub => {
      //     sub.products.forEach(prod => {
      //       products.push(prod);
      //     });
      //   });
      // });
      priceRes.data.data.forEach(category => {
        category.subcategories.forEach(sub => {
          sub.products.forEach(prod => {
            products.push({
              ...prod,
              categoryName: category.name   // ðŸ”¥ attach category
            });
          });
        });
      });
      setProductList(products);
      setCategoryList(catRes.data.categories || []);
    });
  }, []);

  //     setLoading(true);

  //     try {
  //       const res = await axios.get(`${API_BASE}/prices`);

  //       let data = [];

  //       // ðŸ”¥ Flatten Category â†’ Sub â†’ Products
  //       // res.data.data.forEach(category => {
  //       //   category.subcategories.forEach(sub => {
  //       //     sub.products.forEach(prod => {
  //       //       data.push(prod);
  //       //     });
  //       //   });
  //       // });

  //       priceRes.data.data.forEach(category => {
  //   category.subcategories.forEach(sub => {
  //     sub.products.forEach(prod => {
  //       products.push({
  //         ...prod,
  //         categoryName: category.name   // ðŸ”¥ attach category
  //       });
  //     });
  //   });
  // });

  //       // ðŸ”¥ Filters
  //       if (selectedProduct) {
  //         data = data.filter(p => p.name === selectedProduct);
  //       }

  //       // if (selectedCategory) {
  //       //   data = data.filter(p => p.category?.name === selectedCategory);
  //       // }



  //       // ðŸ”¥ Sort by time
  //       data.sort(
  //         (a, b) =>
  //           new Date(a.createdAt || a.validTill) -
  //           new Date(b.createdAt || b.validTill)
  //       );

  //       const labels = data.map(item =>
  //         new Date(item.createdAt || item.validTill).toLocaleString("en-IN")
  //       );

  //       const prices = data.map(item => {
  //         const base = Number(item.basePrice) || 0;
  //         const diff = Number(item.difference) || 0;
  //         return base + diff;
  //       });

  //       // Single point fix
  //       if (labels.length === 1) {
  //         labels.unshift("Earlier");
  //         prices.unshift(prices[0]);
  //       }

  //       setChartData({
  //         labels,
  //         datasets: [
  //           {
  //             label: "Final Price (â‚¹)",
  //             data: prices,
  //             borderColor: "#dc3545",
  //             backgroundColor: "rgba(220,53,69,0.15)",
  //             tension: 0,
  //             fill: true,
  //             pointRadius: 5,
  //             pointHoverRadius: 7,
  //           },
  //         ],
  //       });

  //     } catch (err) {
  //       console.error("Chart error:", err);
  //     } finally {
  //       setLoading(false);
  //     }
  //   };
  const fetchChart = async () => {
    setLoading(true);

    try {
      const res = await axios.get(`${API_BASE}/prices`);

      let data = [];

      // âœ… Proper flatten with category name
      res.data.data.forEach(category => {
        category.subcategories.forEach(sub => {
          sub.products.forEach(prod => {
            data.push({
              ...prod,
              categoryName: category.name   // ðŸ”¥ important
            });
          });
        });
      });

      // âœ… Product filter
      if (selectedProduct) {
        data = data.filter(p => p.name === selectedProduct);
      }

      // âœ… Category filter
      if (selectedCategory) {
        data = data.filter(p => p.categoryName === selectedCategory);
      }

      // â— If nothing found, clear chart
      if (data.length === 0) {
        setChartData(null);
        setLoading(false);
        return;
      }

      // âœ… Sort by time
      data.sort(
        (a, b) =>
          new Date(a.createdAt || a.validTill) -
          new Date(b.createdAt || b.validTill)
      );

      const labels = data.map(item =>
        new Date(item.createdAt || item.validTill).toLocaleString("en-IN")
      );

      const prices = data.map(item => {
        const base = Number(item.basePrice) || 0;
        const diff = Number(item.difference) || 0;
        return base + diff;
      });

      // Single point zigzag fix
      if (labels.length === 1) {
        labels.unshift("Earlier");
        prices.unshift(prices[0]);
      }

      setChartData({
        labels,
        datasets: [
          {
            label: "Final Price (â‚¹)",
            data: prices,
            borderColor: "#dc3545",
            backgroundColor: "rgba(220,53,69,0.15)",
            tension: 0,
            fill: true,
            pointRadius: 5,
            pointHoverRadius: 7,
          },
        ],
      });

    } catch (err) {
      console.error("Chart error:", err);
    } finally {
      setLoading(false);
    }
  };


  useEffect(() => {
    fetchChart();
  }, [selectedProduct, selectedCategory]);

  /* ================= UI ================= */
  return (
    <div className="analytics-container">
      <h2 className="analytics-title">Price Analytics (Zig-Zag)</h2>

      <div className="filter-section">
        <select value={selectedProduct} onChange={e => setSelectedProduct(e.target.value)}>
          <option value="">All Products</option>
          {productList.map(p => (
            <option key={p._id} value={p.name}>{p.name}</option>
          ))}
        </select>

        <select value={selectedCategory} onChange={e => setSelectedCategory(e.target.value)}>
          <option value="">All Categories</option>
          {categoryList.map(c => (
            <option key={c._id} value={c.name}>{c.name}</option>
          ))}
        </select>
      </div>

      <div className="chart-wrapper">
        {loading ? (
          <div className="loading">Loading...</div>
        ) : chartData ? (
          <Line
            data={chartData}
            options={{
              responsive: true,
              maintainAspectRatio: false,
              scales: {
                y: { beginAtZero: false },
              },
              plugins: {
                legend: { position: "bottom" },
                datalabels: {
                  display: true,
                  align: "top",
                  formatter: v => `â‚¹${v}`,
                  font: { weight: "bold" },
                },
              },
            }}
          />
        ) : (
          <div className="no-data">No data available</div>
        )}
      </div>
    </div>
  );
}
