import React, { useState, useEffect } from "react";
import { Link, Outlet, useNavigate } from "react-router-dom";
import {
  Users,
  BarChart2,
  LogOut,
  Menu,
  Grid,
  Package,
  User,
} from "react-feather";

export default function AdminLayout() {
  const navigate = useNavigate();

  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [hoveredLink, setHoveredLink] = useState(null);

  const logout = () => {
    localStorage.clear();
    navigate("/login", { replace: true });
  };

  // ================= RESPONSIVE =================
  useEffect(() => {
    const handler = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener("resize", handler);
    return () => window.removeEventListener("resize", handler);
  }, []);

  useEffect(() => {
    setIsSidebarOpen(!isMobile);
  }, [isMobile]);

  // ================= REDIRECT =================
  useEffect(() => {
    const current = window.location.pathname;
   if (current === "/admin" || current === "/admin/") {
  navigate("/admin/pricelist", { replace: true });
}

  }, [navigate]);

  const hoverStyle = (isHover) => ({
    ...styles.link,
    background: isHover
      ? "linear-gradient(135deg, #983a30 0%, #c0392b 100%)"
      : "transparent",
    color: isHover ? "#fff" : "#e8f1f8",
  });

  const sidebarItems = [
    { to: "/admin", label: "Dashboard", icon: <BarChart2 size={18} /> },
    { to: "/admin/users", label: "Users", icon: <Users size={18} /> },
    {
      to: "/admin/CategoryManager",
      label: "Add Category",
      icon: <Grid size={18} />,
    },
    {
      to: "/admin/priceanalytics",
      label: "Analytics",
      icon: <BarChart2 size={18} />,
    },
    {
      to: "/admin/pricelist",
      label: "Add Product",
      icon: <Package size={18} />,
    },
  ];

  return (
    <div style={{ display: "flex", minHeight: "100vh", background: "#f6f9fc" }}>
      {/* ================= OVERLAY ================= */}
      {isMobile && isSidebarOpen && (
        <div
          onClick={() => setIsSidebarOpen(false)}
          style={{
            position: "fixed",
            inset: 0,
            background: "rgba(0,0,0,0.45)",
            zIndex: 90,
          }}
        />
      )}

      {/* ================= SIDEBAR ================= */}
      <aside
        style={{
          width: 250,
          background: "linear-gradient(180deg, #0f2e47 0%, #1a4d6f 100%)",
          color: "#fff",
          padding: "24px",
          position: "fixed",
          height: "100vh",
          zIndex: 100,
          transform: isSidebarOpen ? "translateX(0)" : "translateX(-100%)",
          transition: "0.35s",
        }}
      >
        <h3>Broker Admin</h3>

        <ul style={{ listStyle: "none", marginTop: 30, padding: 0 }}>
          {sidebarItems.map((item, i) => (
            <li key={i} style={styles.navItem}>
              <Link
                to={item.to}
                style={hoverStyle(hoveredLink === i)}
                onMouseEnter={() => setHoveredLink(i)}
                onMouseLeave={() => setHoveredLink(null)}
                onClick={() => isMobile && setIsSidebarOpen(false)}
              >
                {item.icon} {item.label}
              </Link>
            </li>
          ))}
        </ul>

        <button onClick={logout} style={styles.logout}>
          <LogOut size={18} /> Logout
        </button>
      </aside>

      {/* ================= MAIN ================= */}
      <main
        style={{
          flex: 1,
          marginLeft: !isMobile && isSidebarOpen ? 250 : 0,
          transition: "0.35s",
        }}
      >
        {/* ================= HEADER ================= */}
        <div
          style={{
            background: "#fff",
            padding: "12px 20px",
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
            position: "sticky",
            top: 0,
            zIndex: 50,
          }}
        >
          <button
            onClick={() => setIsSidebarOpen(!isSidebarOpen)}
            style={{ background: "transparent", border: "none", cursor: "pointer" }}
          >
            <Menu size={26} />
          </button>

          <h3 style={{ margin: 0 }}>Admin Dashboard</h3>

          {/* ðŸ” PROFILE â†’ UPDATE PASSWORD */}
          <div
            onClick={() => navigate("/admin/update-password")}
            style={{
              width: 40,
              height: 40,
              borderRadius: "50%",
              background: "#f1f5f9",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              cursor: "pointer",
              border: "1px solid #e5e7eb",
            }}
            title="Update Password"
          >
            <User size={20} />
          </div>
        </div>

        {/* ================= CONTENT ================= */}
        <div style={{ padding: 24 }}>
          <Outlet />
        </div>
      </main>
    </div>
  );
}

const styles = {
  navItem: { padding: "12px 0" },
  link: {
    display: "flex",
    alignItems: "center",
    gap: 12,
    color: "#e8f1f8",
    textDecoration: "none",
    fontSize: 15,
    fontWeight: 500,
    padding: "10px 12px",
    borderRadius: 8,
    transition: "0.25s",
  },
  logout: {
    background: "linear-gradient(135deg, #983a30 0%, #c0392b 100%)",
    border: "none",
    padding: "12px",
    color: "#fff",
    borderRadius: 8,
    marginTop: 20,
    cursor: "pointer",
    width: "100%",
    fontWeight: 600,
    display: "flex",
    justifyContent: "center",
    gap: 8,
  },
};
