import React, { useState, useEffect } from "react";
import { Link, Outlet, useNavigate, useLocation } from "react-router-dom";
import {
  Users, BarChart2, LogOut, Menu, Grid, Package, User, CreditCard, ChevronRight
} from "react-feather";

export default function AdminLayout() {
  const navigate = useNavigate();
  const location = useLocation();

  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 1024);

  const logout = () => {
    localStorage.clear();
    navigate("/login", { replace: true });
  };

  useEffect(() => {
    const handler = () => {
      const mobile = window.innerWidth < 1024;
      setIsMobile(mobile);
      if (mobile) setIsSidebarOpen(false);
      else setIsSidebarOpen(true);
    };
    window.addEventListener("resize", handler);
    return () => window.removeEventListener("resize", handler);
  }, []);

  const sidebarItems = [
    { to: "/admin", label: "Dashboard", icon: <Grid size={19} /> },
    { to: "/admin/users", label: "Users", icon: <Users size={19} /> },
    { to: "/admin/CategoryManager", label: "Categories", icon: <Grid size={19} /> },
    { to: "/admin/priceanalytics", label: "Analytics", icon: <BarChart2 size={19} /> },
    { to: "/admin/pricelist", label: "Add Product", icon: <Package size={19} /> },
    { to: "/admin/orders", label: "Orders", icon: <Package size={19} /> },
    { to: "/admin/payments", label: "Payments", icon: <CreditCard size={19} /> },
    { to: "/admin/gst", label: "GST & Discounts", icon: <CreditCard size={19} /> },
    { to: "/admin/inventory", label: "Inventory", icon: <Package size={19} /> }
  ];

  return (
    <div style={styles.container}>
      {isMobile && isSidebarOpen && (
        <div onClick={() => setIsSidebarOpen(false)} style={styles.overlay} />
      )}

      {/* ================= SIDEBAR ================= */}
      <aside style={{
        ...styles.sidebar,
        transform: isSidebarOpen ? "translateX(0)" : "translateX(-100%)",
        width: "290px"
      }}>
        <div style={styles.logoArea}>
          <div style={styles.logoIcon}>
            <BarChart2 color="white" size={20} fill="white" />
          </div>
          <span style={styles.logoText}>TailAdmin</span>
        </div>

        <div style={styles.menuLabel}>MENU</div>

        <nav style={styles.navStack}>
          {sidebarItems.map((item, i) => {
            const active = location.pathname === item.to;
            return (
              <Link
                key={i}
                to={item.to}
                style={{
                  ...styles.navLink,
                  backgroundColor: active ? "#F3F5FF" : "transparent",
                  color: active ? "#3C50E0" : "#64748B",
                }}
                onClick={() => isMobile && setIsSidebarOpen(false)}
              >
                <div style={styles.linkContent}>
                  <span style={{ display: 'flex', color: active ? "#3C50E0" : "#64748B" }}>
                    {item.icon}
                  </span>
                  <span style={{ fontWeight: active ? "600" : "500" }}>{item.label}</span>
                </div>
                {active && <div style={styles.activeIndicator} />}
              </Link>
            );
          })}
        </nav>

        <div style={styles.sidebarFooter}>
          <button onClick={logout} style={styles.logoutBtn}>
            <LogOut size={18} /> <span>Logout</span>
          </button>
        </div>
      </aside>

      {/* ================= MAIN ================= */}
      <main style={{
        ...styles.main,
        marginLeft: !isMobile && isSidebarOpen ? "290px" : 0,
      }}>
        <header style={styles.header}>
          <button onClick={() => setIsSidebarOpen(!isSidebarOpen)} style={styles.menuBtn}>
            <Menu size={22} color="#64748B" />
          </button>

          <div style={styles.headerRight}>
            <div onClick={() => navigate("/admin/update-password")} style={styles.profileBtn}>
              <User size={20} color="#64748B" />
            </div>
          </div>
        </header>

        <div style={styles.pageBody}>
          <div style={styles.card}>
            <Outlet />
          </div>
        </div>
      </main>
    </div>
  );
}

