import React, { useState, useEffect } from "react";
import { Link, Outlet, useNavigate, useLocation } from "react-router-dom";
import {
  Users,
  BarChart2,
  LogOut,
  Menu,
  Grid,
  Package,
  User,
  CreditCard,
  X,
  ChevronRight,
  Settings
} from "react-feather";

export default function AdminLayout() {
  const navigate = useNavigate();
  const location = useLocation();

  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 1024);
  const [scrolled, setScrolled] = useState(false);

  /* ================= HANDLERS ================= */
  const logout = () => {
    localStorage.clear();
    navigate("/login", { replace: true });
  };

  useEffect(() => {
    const handleResize = () => {
      const mobile = window.innerWidth < 1024;
      setIsMobile(mobile);
      if (!mobile) setIsSidebarOpen(true);
      else setIsSidebarOpen(false);
    };

    const handleScroll = () => setScrolled(window.scrollY > 10);

    window.addEventListener("resize", handleResize);
    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("resize", handleResize);
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  const sidebarItems = [
    { to: "/admin", label: "Dashboard", icon: <BarChart2 size={20} /> },
    { to: "/admin/users", label: "Users", icon: <Users size={20} /> },
    { to: "/admin/CategoryManager", label: "Categories", icon: <Grid size={20} /> },
    { to: "/admin/priceanalytics", label: "Analytics", icon: <BarChart2 size={20} /> },
    { to: "/admin/pricelist", label: "Products", icon: <Package size={20} /> },
    { to: "/admin/orders", label: "Orders", icon: <Package size={20} /> },
    { to: "/admin/payments", label: "Payments", icon: <CreditCard size={20} /> },
    { to: "/admin/gst", label: "GST & Discounts", icon: <Settings size={20} /> },
    { to: "/admin/inventory", label: "Inventory", icon: <Package size={20} /> }
  ];

  return (
    <div style={{ display: "flex", minHeight: "100vh", background: "#f8fafc", fontFamily: "'Inter', sans-serif" }}>

      {/* MOBILE OVERLAY */}
      {isMobile && isSidebarOpen && (
        <div
          onClick={() => setIsSidebarOpen(false)}
          style={styles.overlay}
        />
      )}

      {/* ================= SIDEBAR ================= */}
      <aside
        style={{
          ...styles.sidebar,
          transform: isSidebarOpen ? "translateX(0)" : "translateX(-100%)",
          width: isMobile ? "280px" : "260px",
        }}
      >
        <div style={styles.sidebarHeader}>
          <div style={styles.logoContainer}>
            <div style={styles.logoIcon}>SG</div>
            <h2 style={styles.logoText}>Overseas</h2>
          </div>
          {isMobile && <X onClick={() => setIsSidebarOpen(false)} style={{ cursor: 'pointer' }} />}
        </div>

        <nav style={styles.navContainer}>
          <p style={styles.navSubtitle}>MAIN MENU</p>
          <ul style={{ listStyle: "none", padding: 0, margin: 0 }}>
            {sidebarItems.map((item, i) => {
              const active = location.pathname === item.to;
              return (
                <li key={i} style={{ marginBottom: "4px" }}>
                  <Link
                    to={item.to}
                    style={{
                      ...styles.navLink,
                      backgroundColor: active ? "rgba(255, 255, 255, 0.1)" : "transparent",
                      color: active ? "#fff" : "#94a3b8",
                      borderLeft: active ? "4px solid #3b82f6" : "4px solid transparent",
                    }}
                    onClick={() => isMobile && setIsSidebarOpen(false)}
                  >
                    <span style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
                      {item.icon}
                      {item.label}
                    </span>
                    {active && <ChevronRight size={14} />}
                  </Link>
                </li>
              );
            })}
          </ul>
        </nav>

        <div style={styles.sidebarFooter}>
          <button onClick={logout} style={styles.logoutBtn}>
            <LogOut size={18} /> <span>Logout Session</span>
          </button>
        </div>
      </aside>

      {/* ================= MAIN CONTENT ================= */}
      <main
        style={{
          flex: 1,
          marginLeft: !isMobile && isSidebarOpen ? "260px" : 0,
          transition: "0.3s ease",
          width: "100%"
        }}
      >
        {/* HEADER */}
        <header style={{
          ...styles.header,
          boxShadow: scrolled ? "0 4px 6px -1px rgba(0,0,0,0.1)" : "none",
          borderBottom: scrolled ? "none" : "1px solid #e2e8f0"
        }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '15px' }}>
            <button onClick={() => setIsSidebarOpen(!isSidebarOpen)} style={styles.menuBtn}>
              <Menu size={22} color="#64748b" />
            </button>
            <h2 style={styles.headerTitle}>Dashboard</h2>
          </div>

          <div style={styles.headerActions}>
            <div
              onClick={() => navigate("/admin/update-password")}
              style={styles.profileCircle}
              title="Profile Settings"
            >
              <User size={20} color="#3b82f6" />
            </div>
          </div>
        </header>

        {/* PAGE CONTENT */}
        <div style={styles.contentArea}>
          <div style={styles.contentWrapper}>
            <Outlet />
          </div>
        </div>
      </main>
    </div>
  );
}

