import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { Search, Filter, MoreHorizontal, Edit, Trash2, ChevronDown, ChevronUp, Plus, X } from "react-feather";
import "./CategoryManager.css";

const API_BASE = "https://grocerrybackend.onrender.com/api/categories";

const emptyCategoryForm = { name: "", image: null };
const emptySubForm = { name: "", image: null };

const CategoryManager = () => {
  const [categories, setCategories] = useState([]);
  const [catForm, setCatForm] = useState(emptyCategoryForm);
  const [editCatId, setEditCatId] = useState(null);
  const [showCatForm, setShowCatForm] = useState(false);
  const [openMenuId, setOpenMenuId] = useState(null);
  const [expandedCat, setExpandedCat] = useState(null);
  const [subForm, setSubForm] = useState(emptySubForm);
  const [editSubInfo, setEditSubInfo] = useState({ catId: null, subId: null });
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 6;
  const [searchText, setSearchText] = useState("");
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalMode, setModalMode] = useState("category");
  const [modalTarget, setModalTarget] = useState({ catId: null, subId: null });
  const [modalPreview, setModalPreview] = useState(null);
  const fileInputRef = useRef(null);

  const fetchCategories = async () => {
    try {
      const res = await axios.get(API_BASE);
      const cats = res.data.categories || res.data.data || [];
      setCategories(cats.map((c) => ({
        ...c,
        subcategories: Array.isArray(c.subcategories) ? c.subcategories : [],
      })));
    } catch (err) {
      toast.error("Failed to load categories");
    }
  };

  useEffect(() => { fetchCategories(); }, []);

  const filterCategories = (cats, q) => {
    if (!q || !q.trim()) return cats;
    const t = q.toLowerCase();
    return cats.map((c) => {
      const matchedSubs = c.subcategories.filter((s) => s.name.toLowerCase().includes(t));
      const categoryMatches = c.name.toLowerCase().includes(t);
      if (categoryMatches) return c;
      if (matchedSubs.length) return { ...c, subcategories: matchedSubs };
      return null;
    }).filter(Boolean);
  };

  const onCatInputChange = (e) => {
    const { name, value, files } = e.target;
    if (files) setCatForm((p) => ({ ...p, image: files[0] }));
    else setCatForm((p) => ({ ...p, [name]: value }));
  };

  const onSubInputChange = (e) => {
    const { name, value, files } = e.target;
    if (files) setSubForm((p) => ({ ...p, image: files[0] }));
    else setSubForm((p) => ({ ...p, [name]: value }));
  };

  const submitCategory = async (e) => {
    e.preventDefault();
    if (!catForm.name.trim()) return toast.warn("Enter category name");
    const fd = new FormData();
    fd.append("name", catForm.name);
    if (catForm.image) fd.append("image", catForm.image);
    try {
      if (editCatId) {
        await axios.put(`${API_BASE}/${editCatId}`, fd);
        toast.success("Category updated");
      } else {
        await axios.post(API_BASE, fd);
        toast.success("Category added");
      }
      setCatForm(emptyCategoryForm);
      setEditCatId(null);
      setShowCatForm(false);
      fetchCategories();
    } catch (err) { toast.error("Failed to save category"); }
  };

  const deleteCategory = async (id) => {
    if (!window.confirm("Delete this category?")) return;
    try {
      await axios.delete(`${API_BASE}/${id}`);
      toast.success("Category deleted");
      fetchCategories();
    } catch { toast.error("Failed to delete category"); }
  };

  const submitSub = async (e, catId) => {
    e.preventDefault();
    if (!subForm.name.trim()) return toast.warn("Enter subcategory name");
    const fd = new FormData();
    fd.append("name", subForm.name);
    if (subForm.image) fd.append("image", subForm.image);
    try {
      if (editSubInfo.subId) {
        await axios.put(`${API_BASE}/${editSubInfo.catId}/sub/${editSubInfo.subId}`, fd);
        toast.success("Subcategory updated");
      } else {
        await axios.post(`${API_BASE}/${catId}/sub`, fd);
        toast.success("Subcategory added");
      }
      setSubForm(emptySubForm);
      setEditSubInfo({ catId: null, subId: null });
      fetchCategories();
    } catch { toast.error("Failed to save subcategory"); }
  };

  const deleteSub = async (catId, subId) => {
    if (!window.confirm("Delete this subcategory?")) return;
    try {
      await axios.delete(`${API_BASE}/${catId}/sub/${subId}`);
      toast.success("Subcategory deleted");
      fetchCategories();
    } catch { toast.error("Failed to delete subcategory"); }
  };

  const openCategoryModal = (cat) => {
    setModalMode("category");
    setModalTarget({ catId: cat._id, subId: null });
    setModalPreview(cat.image);
    setCatForm({ name: cat.name, image: null });
    setIsModalOpen(true);
  };

  const openSubModal = (cat, sub) => {
    setModalMode("subcategory");
    setModalTarget({ catId: cat._id, subId: sub._id });
    setModalPreview(sub.image);
    setSubForm({ name: sub.name, image: null });
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setModalPreview(null);
    setCatForm(emptyCategoryForm);
    setSubForm(emptySubForm);
  };

  const saveModal = async (e) => {
    e.preventDefault();
    try {
      const fd = new FormData();
      if (modalMode === "category") {
        fd.append("name", catForm.name);
        if (catForm.image) fd.append("image", catForm.image);
        await axios.put(`${API_BASE}/${modalTarget.catId}`, fd);
      } else {
        fd.append("name", subForm.name);
        if (subForm.image) fd.append("image", subForm.image);
        await axios.put(`${API_BASE}/${modalTarget.catId}/sub/${modalTarget.subId}`, fd);
      }
      toast.success("Updated successfully");
      closeModal();
      fetchCategories();
    } catch { toast.error("Update failed"); }
  };

  const filtered = filterCategories(categories, searchText);
  const totalPages = Math.max(1, Math.ceil(filtered.length / itemsPerPage));
  const lastIndex = currentPage * itemsPerPage;
  const firstIndex = lastIndex - itemsPerPage;
  const currentCats = filtered.slice(firstIndex, lastIndex);

  return (
    <div className="tm-container">
      <ToastContainer />

      {/* HEADER SECTION */}
      <div className="tm-header">
        <div className="tm-title-area">
          <h1>Products Category</h1>
          <p>Manage your categories and subcategories easily.</p>
        </div>
        <button className="tm-add-btn" onClick={() => setShowCatForm(!showCatForm)}>
          <Plus size={18} /> Add Category
        </button>
      </div>

      {/* FILTER & SEARCH BAR */}
      <div className="tm-toolbar">
        <div className="tm-search-wrapper">
          <Search size={18} className="tm-search-icon" />
          <input
            type="text"
            placeholder="Search categories..."
            value={searchText}
            onChange={(e) => setSearchText(e.target.value)}
          />
        </div>
        <button className="tm-filter-btn">
          <Filter size={18} /> Filter
        </button>
      </div>

      {/* ADD CATEGORY FORM INLINE */}
      {showCatForm && (
        <div className="tm-inline-form-card">
          <form onSubmit={submitCategory}>
            <input name="name" placeholder="Category Name" value={catForm.name} onChange={onCatInputChange} />
            <input type="file" onChange={onCatInputChange} />
            <button type="submit" className="tm-submit-inline">Save</button>
            <button type="button" className="tm-close-inline" onClick={() => setShowCatForm(false)}><X size={18} /></button>
          </form>
        </div>
      )}

      {/* TABLE AREA */}
      <div className="tm-card">
        <div className="tm-table-responsive">
          <table className="tm-table">
            <thead>
              <tr>
                <th><input type="checkbox" /></th>
                <th>Category</th>
                <th>Sub-Count</th>
                <th>Status</th>
                <th style={{ textAlign: 'right' }}>Actions</th>
              </tr>
            </thead>
            <tbody>
              {currentCats.map((cat) => (
                <React.Fragment key={cat._id}>
                  <tr className={expandedCat === cat._id ? "active-row" : ""}>
                    <td><input type="checkbox" /></td>
                    <td>
                      <div className="tm-product-cell">
                        <img src={cat.image || "https://via.placeholder.com/50"} alt="" />
                        <span>{cat.name}</span>
                      </div>
                    </td>
                    <td>
                      <button className="tm-manage-btn" onClick={() => setExpandedCat(expandedCat === cat._id ? null : cat._id)}>
                        {cat.subcategories.length} Items {expandedCat === cat._id ? <ChevronUp size={14} /> : <ChevronDown size={14} />}
                      </button>
                    </td>
                    <td>
                      <span className="tm-badge tm-badge-success">Active</span>
                    </td>
                    <td style={{ textAlign: 'right' }}>
                      <div className="tm-action-dots">
                        <MoreHorizontal size={20} onClick={() => setOpenMenuId(openMenuId === cat._id ? null : cat._id)} />
                        {openMenuId === cat._id && (
                          <div className="tm-dropdown">
                            <button onClick={() => openCategoryModal(cat)}><Edit size={14} /> Edit</button>
                            <button onClick={() => deleteCategory(cat._id)} className="text-red"><Trash2 size={14} /> Delete</button>
                          </div>
                        )}
                      </div>
                    </td>
                  </tr>

                  {/* SUB-CATEGORY ROW */}
                  {expandedCat === cat._id && (
                    <tr className="tm-expanded-row">
                      <td colSpan="5">
                        <div className="tm-sub-container">
                          {cat.subcategories.map(sub => (
                            <div key={sub._id} className="tm-sub-item">
                              <img src={sub.image || "https://via.placeholder.com/40"} alt="" />
                              <span>{sub.name}</span>
                              <div className="tm-sub-actions">
                                <Edit size={14} onClick={() => openSubModal(cat, sub)} />
                                <Trash2 size={14} className="text-red" onClick={() => deleteSub(cat._id, sub._id)} />
                              </div>
                            </div>
                          ))}
                          <form className="tm-add-sub-inline" onSubmit={(e) => submitSub(e, cat._id)}>
                            <input placeholder="Add sub-category..." value={subForm.name} name="name" onChange={onSubInputChange} />
                            <button type="submit">Add</button>
                          </form>
                        </div>
                      </td>
                    </tr>
                  )}
                </React.Fragment>
              ))}
            </tbody>
          </table>
        </div>

        {/* PAGINATION */}
        <div className="tm-pagination">
          <button disabled={currentPage === 1} onClick={() => setCurrentPage(p => p - 1)}>Prev</button>
          <span>Page {currentPage} of {totalPages}</span>
          <button disabled={currentPage === totalPages} onClick={() => setCurrentPage(p => p + 1)}>Next</button>
        </div>
      </div>

      {/* MODAL - TAILADMIN STYLE */}
      {isModalOpen && (
        <div className="tm-modal-overlay">
          <div className="tm-modal">
            <div className="tm-modal-header">
              <h3>{modalMode === 'category' ? 'Edit Category' : 'Edit Subcategory'}</h3>
              <X onClick={closeModal} className="cursor-pointer" />
            </div>
            <form onSubmit={saveModal} className="tm-modal-body">
              <label>Name</label>
              <input value={modalMode === 'category' ? catForm.name : subForm.name}
                onChange={(e) => modalMode === 'category' ? setCatForm({ ...catForm, name: e.target.value }) : setSubForm({ ...subForm, name: e.target.value })}
              />
              <label>Image</label>
              <input type="file" onChange={(e) => {
                const file = e.target.files[0];
                if (file) {
                  setModalPreview(URL.createObjectURL(file));
                  modalMode === 'category' ? setCatForm({ ...catForm, image: file }) : setSubForm({ ...subForm, image: file });
                }
              }} />
              {modalPreview && <img src={modalPreview} className="tm-modal-preview" alt="" />}
              <div className="tm-modal-footer">
                <button type="button" className="tm-btn-secondary" onClick={closeModal}>Cancel</button>
                <button type="submit" className="tm-btn-primary">Update Changes</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default CategoryManager;
