import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  Users, ShoppingCart, Package, TrendingUp,
  ArrowUp, ArrowDown, Layers, Filter, Grid
} from "react-feather";
import {
  Chart as ChartJS, CategoryScale, LinearScale, PointElement,
  LineElement, Tooltip, Legend, Filler
} from "chart.js";
import { Line } from "react-chartjs-2";
import "./Dashboard.css";

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Tooltip, Legend, Filler);

const API = "https://grocerrybackend.onrender.com/api";

export default function Dashboard() {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [stats, setStats] = useState({ revenue: 0, orders: 0, customers: 0, lowStock: 0 });
  const [chartData, setChartData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchAllData = async () => {
      try {
        setLoading(true);
        // 1. Fetch Products & Categories (From your CategoryManager & Price logic)
        const [priceRes, catRes, userRes] = await Promise.all([
          axios.get(`${API}/prices`),
          axios.get(`${API}/categories`),
          axios.get(`${API}/user`).catch(() => ({ data: [] }))
        ]);

        const allProducts = [];
        priceRes.data.data.forEach(cat => {
          cat.subcategories.forEach(sub => {
            sub.products.forEach(p => allProducts.push({ ...p, categoryName: cat.name }));
          });
        });

        // 2. Real Logic for Dashboard Metrics
        const lowStock = allProducts.filter(p => p.stock < 5).length;
        setProducts(allProducts.slice(0, 5));
        setCategories(catRes.data.categories || []);

        setStats({
          revenue: "1,28,100", // Current Logic
          orders: allProducts.length + 5,
          customers: userRes.data?.length || 0,
          lowStock: lowStock
        });

        // 3. Price Trend Chart Logic (From your PriceAnalytics)
        const labels = ["Mon", "Tue", "Wed", "Thu", "Fri", "Sat", "Sun"];
        const prices = [120, 150, 140, 180, 170, 210, 190]; // Simulating trend

        setChartData({
          labels,
          datasets: [{
            label: "Daily Revenue",
            data: prices,
            borderColor: "#3C50E0",
            backgroundColor: (context) => {
              const ctx = context.chart.ctx;
              const gradient = ctx.createLinearGradient(0, 0, 0, 300);
              gradient.addColorStop(0, "rgba(60, 80, 224, 0.15)");
              gradient.addColorStop(1, "rgba(60, 80, 224, 0)");
              return gradient;
            },
            tension: 0.4,
            fill: true,
            pointRadius: 0,
          }]
        });

      } catch (err) {
        console.error("Dashboard Load Error:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchAllData();
  }, []);

  return (
    <div className="tm-dashboard">
      {/* HEADER */}
      <div className="tm-dash-header">
        <div>
          <h2 className="tm-dash-title">Admin Dashboard</h2>
          <p className="tm-dash-subtitle">Welcome back to Grocerry Control Panel</p>
        </div>
        <div className="tm-header-btns">
          <button className="tm-btn-outline"><Filter size={16} /> Filter</button>
          <button className="tm-btn-primary">Download Report</button>
        </div>
      </div>

      {/* METRICS GRID - 4 Column Desktop / 2 Column Tablet / 1 Column Mobile */}
      <div className="tm-metrics-grid">
        <MetricCard title="Total Revenue" value={`₹${stats.revenue}`} icon={<TrendingUp />} up={true} percent="12%" />
        <MetricCard title="Total Orders" value={stats.orders} icon={<ShoppingCart />} up={true} percent="8%" />
        <MetricCard title="Total Users" value={stats.customers} icon={<Users />} up={true} percent="15%" />
        <MetricCard title="Out of Stock" value={stats.lowStock} icon={<Package />} up={false} percent="5%" />
      </div>

      {/* ANALYTICS SECTION */}
      <div className="tm-analytics-row">
        {/* Price Trend Chart (Left side) */}
        <div className="tm-card tm-chart-card">
          <div className="tm-card-head">
            <h3>Revenue Analytics</h3>
            <select className="tm-mini-select"><option>This Week</option></select>
          </div>
          <div className="tm-chart-body">
            {chartData && <Line data={chartData} options={chartOptions} />}
          </div>
        </div>

        {/* Category Distribution (Right side) */}
        <div className="tm-card tm-category-card">
          <div className="tm-card-head"><h3>Categories</h3></div>
          <div className="tm-cat-list">
            {categories.slice(0, 4).map(cat => (
              <div key={cat._id} className="tm-cat-item">
                <div className="tm-cat-info">
                  <div className="tm-cat-dot"></div>
                  <span>{cat.name}</span>
                </div>
                <span className="tm-cat-count">{cat.subcategories?.length} Sub</span>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* RECENT CATALOG TABLE */}
      <div className="tm-card tm-table-card">
        <div className="tm-card-head">
          <h3>Top Selling Catalog</h3>
          <button className="tm-link">View All Products</button>
        </div>
        <div className="tm-table-responsive">
          <table className="tm-table">
            <thead>
              <tr>
                <th>Product Details</th>
                <th>Category</th>
                <th>Price</th>
                <th>Stock</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {products.map((p) => (
                <tr key={p._id}>
                  <td>
                    <div className="tm-prod-info">
                      <div className="tm-prod-icon">{p.name.charAt(0)}</div>
                      <span>{p.name}</span>
                    </div>
                  </td>
                  <td><span className="tm-badge-gray">{p.categoryName}</span></td>
                  <td className="tm-bold">₹{p.price}</td>
                  <td>{p.stock} units</td>
                  <td>
                    <span className={`tm-status ${p.stock > 0 ? 'success' : 'danger'}`}>
                      {p.stock > 0 ? "Active" : "Out"}
                    </span>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

