function MetricCard({ title, value, icon, up, percent }) {
  return (
    <div className="tm-metric-box">
      <div className="tm-metric-icon-box">{icon}</div>
      <div className="tm-metric-data">
        <h3 className="tm-metric-val">{value}</h3>
        <p className="tm-metric-label">{title}</p>
        <div className={`tm-metric-trend ${up ? 'up' : 'down'}`}>
          {up ? <ArrowUp size={12} /> : <ArrowDown size={12} />} {percent}
        </div>
      </div>
    </div>
  );
}
