import React, { useState, useEffect } from "react";
import { Link, Outlet, useNavigate, useLocation } from "react-router-dom";
import {
  Users, BarChart2, LogOut, Menu, Grid, Package, User, CreditCard, ChevronDown, ChevronUp
} from "react-feather";

export default function AdminLayout() {
  const navigate = useNavigate();
  const location = useLocation();

  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 1024);

  const logout = () => {
    localStorage.clear();
    navigate("/login", { replace: true });
  };

  useEffect(() => {
    const handler = () => {
      const mobile = window.innerWidth < 1024;
      setIsMobile(mobile);
      if (mobile) setIsSidebarOpen(false);
      else setIsSidebarOpen(true);
    };
    window.addEventListener("resize", handler);
    return () => window.removeEventListener("resize", handler);
  }, []);

  const sidebarItems = [
    { to: "/admin", label: "Dashboard", icon: <Grid size={20} /> },
    { to: "/admin/users", label: "Users", icon: <Users size={20} /> },
    { to: "/admin/CategoryManager", label: "Categories", icon: <Grid size={20} /> },
    { to: "/admin/priceanalytics", label: "Analytics", icon: <BarChart2 size={20} /> },
    { to: "/admin/pricelist", label: "Add Product", icon: <Package size={20} /> },
    { to: "/admin/orders", label: "Orders", icon: <Package size={20} /> },
    { to: "/admin/payments", label: "Payments", icon: <CreditCard size={20} /> },
    { to: "/admin/gst", label: "GST & Discounts", icon: <CreditCard size={20} /> },
    { to: "/admin/inventory", label: "Inventory", icon: <Package size={20} /> }
  ];

  return (
    <div style={styles.container}>
      {/* MOBILE OVERLAY */}
      {isMobile && isSidebarOpen && (
        <div onClick={() => setIsSidebarOpen(false)} style={styles.overlay} />
      )}

      {/* ================= SIDEBAR (TailAdmin Light Style) ================= */}
      <aside style={{
        ...styles.sidebar,
        transform: isSidebarOpen ? "translateX(0)" : "translateX(-100%)",
        width: "280px"
      }}>
        {/* LOGO SECTION */}
        <div style={styles.logoSection}>
          <div style={styles.logoIcon}>
            <BarChart2 color="white" size={20} />
          </div>
          <h1>SG Overseas</h1>
        </div>

        <div style={styles.menuLabel}>MENU</div>

        <nav style={styles.nav}>
          <ul style={{ listStyle: "none", padding: 0 }}>
            {sidebarItems.map((item, i) => {
              const active = location.pathname === item.to;
              return (
                <li key={i} style={{ marginBottom: "5px" }}>
                  <Link
                    to={item.to}
                    style={{
                      ...styles.link,
                      backgroundColor: active ? "#EFF6FF" : "transparent",
                      color: active ? "#3C50E0" : "#64748B",
                    }}
                    onClick={() => isMobile && setIsSidebarOpen(false)}
                  >
                    <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
                      <span style={{ color: active ? "#3C50E0" : "#64748B" }}>{item.icon}</span>
                      <span style={styles.linkLabel}>{item.label}</span>
                    </div>
                    {active && <ChevronDown size={18} color="#3C50E0" />}
                  </Link>
                </li>
              );
            })}
          </ul>
        </nav>

        {/* LOGOUT AT BOTTOM */}
        <div style={styles.sidebarFooter}>
          <button onClick={logout} style={styles.logoutBtn}>
            <LogOut size={18} /> Logout
          </button>
        </div>
      </aside>

      {/* ================= MAIN CONTENT ================= */}
      <main style={{
        ...styles.main,
        marginLeft: !isMobile && isSidebarOpen ? "280px" : 0,
      }}>
        {/* HEADER */}
        <header style={styles.header}>
          <button onClick={() => setIsSidebarOpen(!isSidebarOpen)} style={styles.toggleBtn}>
            <Menu size={24} color="#64748B" />
          </button>

          <div style={styles.headerRight}>
            <div onClick={() => navigate("/admin/update-password")} style={styles.avatar}>
              <User size={20} color="#64748B" />
            </div>
          </div>
        </header>

        {/* PAGE CONTENT */}
        <div style={styles.content}>
          <Outlet />
        </div>
      </main>
    </div>
  );
}

/* ================= EXACT TAILADMIN STYLES ================= */

