  container: {
    display: "flex",
    minHeight: "100vh",
    backgroundColor: "#F1F5F9", // TailAdmin light bg
    fontFamily: "'Satoshi', sans-serif", // Similar to TailAdmin font
  },
  overlay: {
    position: "fixed",
    inset: 0,
    backgroundColor: "rgba(0,0,0,0.2)",
    zIndex: 90,
  },
  sidebar: {
    position: "fixed",
    height: "100vh",
    backgroundColor: "#FFFFFF",
    zIndex: 100,
    transition: "0.3s cubic-bezier(0.4, 0, 0.2, 1)",
    borderRight: "1px solid #E2E8F0",
    display: "flex",
    flexDirection: "column",
  },
  logoSection: {
    padding: "30px 25px",
    display: "flex",
    alignItems: "center",
    gap: "12px",
  },
  logoIcon: {
    backgroundColor: "#3C50E0",
    width: "34px",
    height: "34px",
    borderRadius: "8px",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },
  logoText: {
    fontSize: "24px",
    fontWeight: "700",
    color: "#1C2434",
    margin: 0,
  },
  menuLabel: {
    padding: "0 25px",
    fontSize: "14px",
    fontWeight: "600",
    color: "#8A99AF",
    marginBottom: "15px",
  },
  nav: {
    flex: 1,
    padding: "0 15px",
  },
  link: {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
    padding: "12px 15px",
    textDecoration: "none",
    borderRadius: "8px",
    transition: "all 0.2s",
    fontSize: "16px",
    fontWeight: "500",
  },
  linkLabel: {
    fontSize: "16px",
  },
  sidebarFooter: {
    padding: "20px",
    borderTop: "1px solid #E2E8F0",
  },
  logoutBtn: {
    width: "100%",
    padding: "12px",
    backgroundColor: "#F1F5F9",
    border: "none",
    borderRadius: "8px",
    color: "#D34053",
    fontWeight: "600",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    gap: "10px",
    cursor: "pointer",
    transition: "0.2s",
  },
  main: {
    flex: 1,
    transition: "0.3s",
    display: "flex",
    flexDirection: "column",
  },
  header: {
    height: "80px",
    backgroundColor: "#FFFFFF",
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
    padding: "0 30px",
    borderBottom: "1px solid #E2E8F0",
    position: "sticky",
    top: 0,
    zIndex: 80,
  },
  toggleBtn: {
    background: "none",
    border: "none",
    cursor: "pointer",
  },
  headerRight: {
    display: "flex",
    alignItems: "center",
  },
  avatar: {
    width: "45px",
    height: "45px",
    borderRadius: "50%",
    backgroundColor: "#EFF4FB",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    cursor: "pointer",
    border: "1px solid #E2E8F0",
  },
  content: {
    padding: "30px",
    flex: 1,
  }
};
