import React from "react";
import { Users, Grid, Package, BarChart2 } from "react-feather";

export default function Dashboard() {
  return (
    <div>
      {/* Page Title */}
      <h2 style={styles.pageTitle}>Dashboard Overview</h2>

      {/* Top Stats */}
      <div style={styles.grid}>
        <StatCard
          title="Total Users"
          value="1,248"
          icon={<Users size={30} />}
          gradient="linear-gradient(135deg, #2563eb, #1e40af)"
        />
        <StatCard
          title="Total Categories"
          value="24"
          icon={<Grid size={30} />}
          gradient="linear-gradient(135deg, #16a34a, #166534)"
        />
        <StatCard
          title="Total Products"
          value="312"
          icon={<Package size={30} />}
          gradient="linear-gradient(135deg, #ea580c, #9a3412)"
        />
        <StatCard
          title="Monthly Sales"
          value="‚Çπ 2.4L"
          icon={<BarChart2 size={30} />}
          gradient="linear-gradient(135deg, #7c3aed, #4c1d95)"
        />
      </div>

      {/* Info Section */}
      <div style={styles.bottomGrid}>
        <InfoCard
          title="Recent Activity"
          items={[
            "New user registered",
            "Product added to catalog",
            "Category updated",
            "Order completed",
          ]}
        />
        <InfoCard
          title="System Status"
          items={[
            "Server running normally",
            "Database connected",
            "No pending alerts",
            "All services operational",
          ]}
        />
      </div>
    </div>
  );
}

/* ================= COMPONENTS ================= */

function StatCard({ title, value, icon, gradient }) {
  return (
    <div style={{ ...styles.card, background: gradient }}>
      <div>
        <p style={styles.cardTitle}>{title}</p>
        <h2 style={styles.cardValue}>{value}</h2>
      </div>
      <div style={styles.icon}>{icon}</div>
    </div>
  );
}

function InfoCard({ title, items }) {
  return (
    <div style={styles.infoCard}>
      <h4 style={{ marginBottom: 10 }}>{title}</h4>
      <ul style={styles.list}>
        {items.map((item, i) => (
          <li key={i} style={styles.listItem}>
            ‚Ä¢ {item}
          </li>
        ))}
      </ul>
    </div>
  );
}

/* ================= STYLES ================= */

const styles = {
  pageTitle: {
    marginBottom: 20,
    fontWeight: 600,
  },
  grid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(220px, 1fr))",
    gap: 20,
    marginBottom: 30,
  },
  card: {
    color: "#fff",
    padding: 22,
    borderRadius: 14,
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    boxShadow: "0 15px 35px rgba(0,0,0,0.15)",
  },
  cardTitle: {
    margin: 0,
    fontSize: 14,
    opacity: 0.9,
  },
  cardValue: {
    margin: "6px 0 0",
    fontSize: 30,
    fontWeight: 700,
  },
  icon: {
    background: "rgba(255,255,255,0.25)",
    padding: 14,
    borderRadius: "50%",
  },
  bottomGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(320px, 1fr))",
    gap: 20,
  },
  infoCard: {
    background: "#fff",
    padding: 20,
    borderRadius: 12,
    boxShadow: "0 10px 25px rgba(0,0,0,0.08)",
  },
  list: {
    listStyle: "none",
    padding: 0,
    margin: 0,
    color: "#374151",
  },
  listItem: {
    padding: "6px 0",
    fontSize: 14,
  },
};
