import React, { useEffect, useState } from "react";
import { Users, Grid, Package } from "react-feather";
import axios from "axios";

export default function Dashboard() {
  const [stats, setStats] = useState({
    users: 0,
    categories: 0,
    products: 0,
  });

  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const token = localStorage.getItem("token");

      // üîÅ Replace these APIs with your real endpoints
      const [usersRes, categoriesRes, productsRes] = await Promise.all([
        axios.get("http://localhost:7000/api/users/count", {
          headers: { Authorization: `Bearer ${token}` },
        }),
        axios.get("http://localhost:7000/api/categories/count", {
          headers: { Authorization: `Bearer ${token}` },
        }),
        axios.get("http://localhost:7000/api/prices/count", {
          headers: { Authorization: `Bearer ${token}` },
        }),
      ]);

      setStats({
        users: usersRes.data.count,
        categories: categoriesRes.data.count,
        products: productsRes.data.count,
      });
    } catch (err) {
      console.error("Dashboard data error", err);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <h3 style={{ textAlign: "center" }}>Loading dashboard...</h3>;
  }

  return (
    <div>
      <h2 style={{ marginBottom: 20 }}>Dashboard Overview</h2>

      <div style={styles.grid}>
        <StatCard
          title="Total Users"
          value={stats.users}
          icon={<Users size={28} />}
          gradient="linear-gradient(135deg, #2563eb, #1e40af)"
        />

        <StatCard
          title="Total Categories"
          value={stats.categories}
          icon={<Grid size={28} />}
          gradient="linear-gradient(135deg, #16a34a, #166534)"
        />

        <StatCard
          title="Total Products"
          value={stats.products}
          icon={<Package size={28} />}
          gradient="linear-gradient(135deg, #ea580c, #9a3412)"
        />
      </div>
    </div>
  );
}

/* ================= CARD COMPONENT ================= */

function StatCard({ title, value, icon, gradient }) {
  return (
    <div style={{ ...styles.card, background: gradient }}>
      <div>
        <p style={styles.cardTitle}>{title}</p>
        <h2 style={styles.cardValue}>{value}</h2>
      </div>
      <div style={styles.icon}>{icon}</div>
    </div>
  );
}

/* ================= STYLES ================= */

const styles = {
  grid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(240px, 1fr))",
    gap: 20,
  },
  card: {
    color: "#fff",
    padding: 20,
    borderRadius: 14,
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    boxShadow: "0 15px 35px rgba(0,0,0,0.15)",
    transition: "transform 0.3s",
  },
  cardTitle: {
    margin: 0,
    fontSize: 14,
    opacity: 0.9,
  },
  cardValue: {
    margin: "6px 0 0",
    fontSize: 30,
    fontWeight: 700,
  },
  icon: {
    background: "rgba(255,255,255,0.2)",
    padding: 14,
    borderRadius: "50%",
  },
};
