import React, { useEffect, useState } from "react";
import axios from "axios";
import { Package, Percent, Hash, Layers, Save, Plus } from "react-feather";
import "./AdminGST.css";

const API = "https://grocerrybackend.onrender.com/api";

export default function AdminGST() {
  const [products, setProducts] = useState([]);
  const [productId, setProductId] = useState("");
  const [gst, setGst] = useState("");
  const [hsn, setHsn] = useState("");
  const [taxType, setTaxType] = useState("cgst_sgst");
  const [minQty, setMinQty] = useState("");
  const [maxQty, setMaxQty] = useState("");
  const [discount, setDiscount] = useState("");
  const [gstList, setGstList] = useState([]);
  const [discountList, setDiscountList] = useState([]);
  const [loading, setLoading] = useState(false);

  const loadProducts = async () => {
    const res = await axios.get(API + "/prices");
    const list = [];
    res.data.data.forEach(c => {
      c.subcategories.forEach(s => {
        s.products.forEach(p => list.push(p));
      });
    });
    setProducts(list);
  };

  const loadGST = async () => {
    const res = await axios.get(API + "/gst/all");
    setGstList(res.data);
  };

  const loadDiscount = async () => {
    const res = await axios.get(API + "/discount/all");
    setDiscountList(res.data);
  };

  useEffect(() => {
    loadProducts();
    loadGST();
    loadDiscount();
  }, []);

  const saveGST = async () => {
    if (!productId || gst === "" || hsn === "") {
      alert("Select product, GST & HSN");
      return;
    }
    setLoading(true);
    try {
      await axios.post(API + "/gst/set", {
        productId,
        gstPercent: Number(gst),
        hsnCode: hsn,
        taxType,
      });
      alert("GST Saved");
      setGst(""); setHsn("");
      loadGST();
    } catch { alert("GST save failed"); }
    setLoading(false);
  };

  const saveDiscount = async () => {
    if (!productId || minQty === "" || maxQty === "" || discount === "") {
      alert("Fill all discount fields");
      return;
    }
    setLoading(true);
    try {
      await axios.post(API + "/discount/add", {
        product: productId,
        minQty: Number(minQty),
        maxQty: Number(maxQty),
        discountPercent: Number(discount),
      });
      alert("Discount Added");
      setMinQty(""); setMaxQty(""); setDiscount("");
      loadDiscount();
    } catch { alert("Discount save failed"); }
    setLoading(false);
  };

  return (
    <div className="tm-gst-container">
      {/* Page Header */}
      <div className="tm-page-header">
        <div>
          <h2 className="tm-page-title">GST & Discount Manager</h2>
          <p className="tm-page-subtitle">Configure tax rules and quantity-based discounts</p>
        </div>
      </div>

      <div className="tm-grid-layout">
        {/* LEFT COLUMN: FORMS */}
        <div className="tm-col-forms">

          {/* Product Selection Card */}
          <div className="tm-card">
            <div className="tm-card-header">
              <h3>Target Product</h3>
            </div>
            <div className="tm-card-body">
              <div className="tm-form-group">
                <label>Select Product</label>
                <div className="tm-input-wrapper">
                  <Package className="tm-input-icon" size={18} />
                  <select value={productId} onChange={e => setProductId(e.target.value)}>
                    <option value="">Choose a product...</option>
                    {products.map(p => (
                      <option key={p._id} value={p._id}>{p.name}</option>
                    ))}
                  </select>
                </div>
              </div>
            </div>
          </div>

          {/* GST Setup Card */}
          <div className="tm-card">
            <div className="tm-card-header">
              <h3>GST Setup</h3>
            </div>
            <div className="tm-card-body">
              <div className="tm-form-grid">
                <div className="tm-form-group">
                  <label>GST %</label>
                  <div className="tm-input-wrapper">
                    <Percent className="tm-input-icon" size={18} />
                    <input type="number" placeholder="18" value={gst} onChange={e => setGst(e.target.value)} />
                  </div>
                </div>
                <div className="tm-form-group">
                  <label>HSN Code</label>
                  <div className="tm-input-wrapper">
                    <Hash className="tm-input-icon" size={18} />
                    <input placeholder="Code" value={hsn} onChange={e => setHsn(e.target.value)} />
                  </div>
                </div>
              </div>
              <div className="tm-form-group">
                <label>Tax Type</label>
                <div className="tm-input-wrapper">
                  <Layers className="tm-input-icon" size={18} />
                  <select value={taxType} onChange={e => setTaxType(e.target.value)}>
                    <option value="cgst_sgst">CGST + SGST</option>
                    <option value="igst">IGST</option>
                  </select>
                </div>
              </div>
              <button className="tm-btn-primary" onClick={saveGST} disabled={loading}>
                <Save size={18} /> {loading ? "Saving..." : "Save GST Rules"}
              </button>
            </div>
          </div>

          {/* Discount Card */}
          <div className="tm-card">
            <div className="tm-card-header">
              <h3>Quantity Discount</h3>
            </div>
            <div className="tm-card-body">
              <div className="tm-form-grid">
                <div className="tm-form-group">
                  <label>Min Qty</label>
                  <input className="tm-basic-input" type="number" placeholder="1" value={minQty} onChange={e => setMinQty(e.target.value)} />
                </div>
                <div className="tm-form-group">
                  <label>Max Qty</label>
                  <input className="tm-basic-input" type="number" placeholder="10" value={maxQty} onChange={e => setMaxQty(e.target.value)} />
                </div>
              </div>
              <div className="tm-form-group">
                <label>Discount %</label>
                <div className="tm-input-wrapper">
                  <Percent className="tm-input-icon" size={18} />
                  <input type="number" placeholder="5" value={discount} onChange={e => setDiscount(e.target.value)} />
                </div>
              </div>
              <button className="tm-btn-secondary" onClick={saveDiscount} disabled={loading}>
                <Plus size={18} /> Add Discount Rule
              </button>
            </div>
          </div>
        </div>

        {/* RIGHT COLUMN: TABLES */}
        <div className="tm-col-tables">

          {/* Saved GST Table */}
          <div className="tm-card">
            <div className="tm-card-header">
              <h3>Active GST Rules</h3>
            </div>
            <div className="tm-table-wrapper">
              <table className="tm-table">
                <thead>
                  <tr>
                    <th>Product</th>
                    <th>HSN</th>
                    <th>GST</th>
                    <th>Type</th>
                  </tr>
                </thead>
                <tbody>
                  {gstList.map(g => (
                    <tr key={g._id}>
                      <td className="tm-font-bold">{g.product?.name}</td>
                      <td><span className="tm-badge-gray">{g.hsnCode}</span></td>
                      <td className="tm-font-bold tm-text-blue">{g.gstPercent}%</td>
                      <td>{g.taxType === "cgst_sgst" ? "CGST+SGST" : "IGST"}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>

          {/* Discount Table */}
          <div className="tm-card">
            <div className="tm-card-header">
              <h3>Active Discounts</h3>
            </div>
            <div className="tm-table-wrapper">
              <table className="tm-table">
                <thead>
                  <tr>
                    <th>Product</th>
                    <th>Range</th>
                    <th>Discount</th>
                  </tr>
                </thead>
                <tbody>
                  {discountList.map(d => (
                    <tr key={d._id}>
                      <td className="tm-font-bold">{d.product?.name}</td>
                      <td>{d.minQty} - {d.maxQty} Qty</td>
                      <td><span className="tm-badge-green">{d.discountPercent}% OFF</span></td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>

        </div>
      </div>
    </div>
  );
}
