import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  Users, ShoppingCart, Package, TrendingUp, ArrowUp, ArrowDown,
  Layers, Filter, Box, AlertTriangle, DollarSign, Archive
} from "react-feather";
import {
  Chart as ChartJS, CategoryScale, LinearScale, PointElement,
  LineElement, Tooltip, Legend, Filler
} from "chart.js";
import { Line } from "react-chartjs-2";
import "./Dashboard.css";

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Tooltip, Legend, Filler);

const API = "https://grocerrybackend.onrender.com/api";

export default function Dashboard() {
  const [products, setProducts] = useState([]);
  const [inventory, setInventory] = useState([]);
  const [categories, setCategories] = useState([]);
  const [userCount, setUserCount] = useState(0);
  const [stats, setStats] = useState({ revenue: 0, lowStock: 0, totalItems: 0 });
  const [chartData, setChartData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        setLoading(true);

        // Parallel API Calls (Sabhi modules ka data)
        const [priceRes, catRes, userRes, invRes] = await Promise.all([
          axios.get(`${API}/prices`),
          axios.get(`${API}/categories`),
          axios.get(`${API}/user`),
          axios.get(`${API}/inventory/all`)
        ]);

        // 1. Process Products (Price Analytics logic)
        const allProds = [];
        priceRes.data.data.forEach(c =>
          c.subcategories.forEach(s =>
            s.products.forEach(p => allProds.push({ ...p, categoryName: c.name }))
          )
        );
        setProducts(allProds);

        // 2. Process Inventory (Inventory Manager logic)
        const invData = invRes.data.data || [];
        setInventory(invData);

        // 3. Process Stats & Logic
        const totalRev = invData.reduce((acc, curr) => acc + (Number(curr.sellingPrice) || 0), 0);
        const lowStockCount = invData.filter(i => i.stock <= (i.minStock || 5)).length;

        setStats({
          revenue: totalRev,
          lowStock: lowStockCount,
          totalItems: invData.length
        });

        setCategories(catRes.data.categories || []);
        setUserCount(userRes.data?.length || 0);

        // 4. Chart Logic (Daily Trend Style)
        setChartData({
          labels: ["Mon", "Tue", "Wed", "Thu", "Fri", "Sat", "Sun"],
          datasets: [{
            label: "Stock Value Trend",
            data: [totalRev * 0.8, totalRev * 0.9, totalRev, totalRev * 0.85, totalRev * 1.1, totalRev * 1.05, totalRev],
            borderColor: "#3C50E0",
            backgroundColor: "rgba(60, 80, 224, 0.1)",
            fill: true,
            tension: 0.4,
            pointRadius: 2
          }]
        });

      } catch (err) {
        console.error("Dashboard Load Error", err);
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, []);

  if (loading) return <div className="tm-loader-full">Loading Admin Insights...</div>;

  return (
    <div className="tm-dashboard-wrapper">
      {/* 1. Header Area */}
      <div className="tm-dash-header">
        <div className="tm-title-section">
          <h2>Dashboard Overview</h2>
          <p>Real-time data from Inventory, Users, and Prices</p>
        </div>
        <div className="tm-header-actions">
          <button className="tm-btn-outline"><Filter size={16} /> Filter</button>
          <button className="tm-btn-primary">Generate Report</button>
        </div>
      </div>

      {/* 2. Top Metrics Grid (4 Column Responsive) */}
      <div className="tm-metrics-grid">
        <MetricCard title="Inventory Value" value={`₹${stats.revenue.toLocaleString()}`} icon={<DollarSign />} up={true} percent="12.5%" color="blue" />
        <MetricCard title="Total Users" value={userCount} icon={<Users />} up={true} percent="4.3%" color="cyan" />
        <MetricCard title="Inventory Items" value={stats.totalItems} icon={<Box />} up={true} percent="2.1%" color="orange" />
        <MetricCard title="Low Stock Alerts" value={stats.lowStock} icon={<AlertTriangle />} up={false} percent="Check Now" color="red" />
      </div>

      <div className="tm-main-grid">
        {/* 3. Graph Section (Price Trend Style) */}
        <div className="tm-card tm-graph-card">
          <div className="tm-card-header">
            <h3>Revenue & Stock Analytics</h3>
            <div className="tm-badge-blue">Weekly</div>
          </div>
          <div className="tm-chart-container">
            {chartData && <Line data={chartData} options={chartOptions} />}
          </div>
        </div>

        {/* 4. Quick Category Summary */}
        <div className="tm-card tm-category-card">
          <div className="tm-card-header"><h3>Categories</h3></div>
          <div className="tm-cat-list">
            {categories.slice(0, 5).map(cat => (
              <div key={cat._id} className="tm-cat-row">
                <div className="tm-cat-icon">{cat.name.charAt(0)}</div>
                <div className="tm-cat-info">
                  <span className="tm-cat-name">{cat.name}</span>
                  <span className="tm-cat-sub">{cat.subcategories?.length} Subcategories</span>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* 5. Inventory Summary Table (Direct from Inventory Module) */}
      <div className="tm-card tm-table-section">
        <div className="tm-card-header">
          <h3>Recent Inventory Status</h3>
          <button className="tm-link-btn">View Full Inventory</button>
        </div>
        <div className="tm-responsive-table">
          <table className="tm-table">
            <thead>
              <tr>
                <th>Product Name</th>
                <th>Stock Level</th>
                <th>Selling Price</th>
                <th>GST</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {inventory.slice(0, 6).map((inv) => (
                <tr key={inv._id}>
                  <td>
                    <div className="tm-prod-cell">
                      <div className="tm-prod-img">{inv.product?.name?.charAt(0) || "P"}</div>
                      <div className="tm-prod-name-box">
                        <span className="tm-bold">{inv.product?.name || "Unknown"}</span>
                        <small>SKU: {inv.sku || 'N/A'}</small>
                      </div>
                    </div>
                  </td>
                  <td>
                    <div className="tm-stock-cell">
                      <span className="tm-bold">{inv.stock}</span>
                      <div className="tm-stock-bar">
                        <div className="tm-stock-fill" style={{ width: `${Math.min(inv.stock, 100)}%`, background: inv.stock < inv.minStock ? '#e11d48' : '#10b981' }}></div>
                      </div>
                    </div>
                  </td>
                  <td className="tm-blue-text">₹{inv.sellingPrice}</td>
                  <td>{inv.gstPercent}%</td>
                  <td>
                    <span className={`tm-status-pill ${inv.stock < inv.minStock ? 'danger' : 'success'}`}>
                      {inv.stock < inv.minStock ? "Low Stock" : "In Stock"}
                    </span>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

// Chart Options Settings
const chartOptions = {
  responsive: true,
  maintainAspectRatio: false,
  plugins: { legend: { display: false } },
  scales: {
    y: { beginAtZero: true, grid: { color: "#f1f5f9" } },
    x: { grid: { display: false } }
  }
};

// Sub-Component: Metric Card
function MetricCard({ title, value, icon, up, percent, color }) {
  return (
    <div className={`tm-metric-card border-${color}`}>
      <div className="tm-metric-icon">{icon}</div>
      <div className="tm-metric-content">
        <p className="tm-metric-label">{title}</p>
        <h3 className="tm-metric-value">{value}</h3>
        <div className={`tm-metric-footer ${up ? 'text-green' : 'text-red'}`}>
          {up ? <ArrowUp size={14} /> : <ArrowDown size={14} />}
          <span>{percent}</span>
        </div>
      </div>
    </div>
  );
}
