import React, { useEffect, useState } from "react";
import axios from "axios";
import { Package, Percent, Hash, Layers, Save, Plus } from "react-feather";
import "./AdminGST.css";

const API = "https://grocerrybackend.onrender.com/api";

export default function AdminGST() {
  const [products, setProducts] = useState([]);
  const [productId, setProductId] = useState("");
  const [gst, setGst] = useState("");
  const [hsn, setHsn] = useState("");
  const [taxType, setTaxType] = useState("cgst_sgst");
  const [minQty, setMinQty] = useState("");
  const [maxQty, setMaxQty] = useState("");
  const [discount, setDiscount] = useState("");
  const [gstList, setGstList] = useState([]);
  const [discountList, setDiscountList] = useState([]);
  const [loading, setLoading] = useState(false);

  const loadProducts = async () => {
    try {
      const res = await axios.get(API + "/prices");
      const list = [];
      res.data.data.forEach(c => {
        c.subcategories.forEach(s => {
          s.products.forEach(p => list.push(p));
        });
      });
      setProducts(list);
    } catch (err) { console.error("Product load error"); }
  };

  const loadGST = async () => {
    try {
      const res = await axios.get(API + "/gst/all");
      setGstList(res.data);
    } catch (err) { console.error("GST load error"); }
  };

  const loadDiscount = async () => {
    try {
      const res = await axios.get(API + "/discount/all");
      setDiscountList(res.data);
    } catch (err) { console.error("Discount load error"); }
  };

  useEffect(() => {
    loadProducts();
    loadGST();
    loadDiscount();
  }, []);

  const saveGST = async () => {
    if (!productId || gst === "" || hsn === "") return alert("Fields missing");
    setLoading(true);
    try {
      await axios.post(API + "/gst/set", { productId, gstPercent: Number(gst), hsnCode: hsn, taxType });
      alert("GST Saved");
      setGst(""); setHsn(""); loadGST();
    } catch { alert("Error"); }
    setLoading(false);
  };

  const saveDiscount = async () => {
    if (!productId || minQty === "" || maxQty === "" || discount === "") return alert("Fields missing");
    setLoading(true);
    try {
      await axios.post(API + "/discount/add", { product: productId, minQty: Number(minQty), maxQty: Number(maxQty), discountPercent: Number(discount) });
      alert("Discount Added");
      setMinQty(""); setMaxQty(""); setDiscount(""); loadDiscount();
    } catch { alert("Error"); }
    setLoading(false);
  };

  return (
    <div className="gst-wrapper">
      <div className="gst-header">
        <h1>GST & Discount Manager</h1>
        <p>Manage tax and quantity offers</p>
      </div>

      <div className="gst-main-grid">
        {/* LEFT: FORMS */}
        <div className="gst-forms-column">
          <div className="gst-card">
            <div className="gst-card-head"><h3>1. Select Product</h3></div>
            <div className="gst-card-body">
              <div className="gst-input-group">
                <label>Target Product</label>
                <div className="gst-icon-input">
                  <Package size={18} />
                  <select value={productId} onChange={e => setProductId(e.target.value)}>
                    <option value="">Select a product</option>
                    {products.map(p => <option key={p._id} value={p._id}>{p.name}</option>)}
                  </select>
                </div>
              </div>
            </div>
          </div>

          <div className="gst-card">
            <div className="gst-card-head"><h3>2. GST Configuration</h3></div>
            <div className="gst-card-body">
              <div className="gst-row">
                <div className="gst-input-group">
                  <label>GST %</label>
                  <div className="gst-icon-input"><Percent size={18} /><input type="number" value={gst} onChange={e => setGst(e.target.value)} /></div>
                </div>
                <div className="gst-input-group">
                  <label>HSN Code</label>
                  <div className="gst-icon-input"><Hash size={18} /><input value={hsn} onChange={e => setHsn(e.target.value)} /></div>
                </div>
              </div>
              <div className="gst-input-group">
                <label>Tax Type</label>
                <div className="gst-icon-input"><Layers size={18} />
                  <select value={taxType} onChange={e => setTaxType(e.target.value)}>
                    <option value="cgst_sgst">CGST + SGST</option>
                    <option value="igst">IGST</option>
                  </select>
                </div>
              </div>
              <button className="gst-btn-primary" onClick={saveGST} disabled={loading}><Save size={18} /> Save GST</button>
            </div>
          </div>

          <div className="gst-card">
            <div className="gst-card-head"><h3>3. Bulk Discount</h3></div>
            <div className="gst-card-body">
              <div className="gst-row">
                <div className="gst-input-group"><label>Min</label><input className="gst-clean-input" type="number" value={minQty} onChange={e => setMinQty(e.target.value)} /></div>
                <div className="gst-input-group"><label>Max</label><input className="gst-clean-input" type="number" value={maxQty} onChange={e => setMaxQty(e.target.value)} /></div>
              </div>
              <div className="gst-input-group">
                <label>Discount %</label>
                <div className="gst-icon-input"><Percent size={18} /><input type="number" value={discount} onChange={e => setDiscount(e.target.value)} /></div>
              </div>
              <button className="gst-btn-success" onClick={saveDiscount} disabled={loading}><Plus size={18} /> Add Discount</button>
            </div>
          </div>
        </div>

        {/* RIGHT: TABLES */}
        <div className="gst-tables-column">
          <div className="gst-card">
            <div className="gst-card-head"><h3>Active GST Rules</h3></div>
            <div className="gst-table-container">
              <table>
                <thead>
                  <tr><th>Product</th><th>HSN</th><th>GST</th></tr>
                </thead>
                <tbody>
                  {gstList.map(g => (
                    <tr key={g._id}>
                      <td><strong>{g.product?.name}</strong></td>
                      <td><span className="gst-tag">{g.hsnCode}</span></td>
                      <td className="gst-text-blue">{g.gstPercent}%</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>

          <div className="gst-card">
            <div className="gst-card-head"><h3>Active Discounts</h3></div>
            <div className="gst-table-container">
              <table>
                <thead>
                  <tr><th>Product</th><th>Range</th><th>Disc</th></tr>
                </thead>
                <tbody>
                  {discountList.map(d => (
                    <tr key={d._id}>
                      <td><strong>{d.product?.name}</strong></td>
                      <td>{d.minQty}-{d.maxQty}</td>
                      <td><span className="gst-tag-green">{d.discountPercent}%</span></td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
